mapper.insert:
	
	INSERT INTO MEMBER_DETAIL (
			MEMBER_ID
			, JOIN_TYPE
			, PASSWORD
			, BIRTH_DT
			, NICK_NAME
			, REGIST_DT
			, REGIST_ID
			, REGIST_IP
			, UPDATE_DT
			, UPDATE_ID
			, UPDATE_IP	
			)
	VALUES(
		#{memberId}
		,#{type}
		, SHA2(#{password},256)
		, #{birth}
		, #{nickName}		
		,SYSDATE()
		,#{memberId}
		,'0.0.0.0'
		,SYSDATE()
		,#{memberId}
		,'0.0.0.0'
	)

mapper.update:
UPDATE MEMBER_DETAIL
SET BIRTH_DT = #{birth}
, NICK_NAME = #{nickName}
WHERE 1=1
 AND MEMBER_ID = #{memberId}
 AND JOIN_TYPE = #{type}
